    });
  } catch (error) {
    console.error("Error using diamonds:", error);
    res.status(500).json({ message: "Failed to use diamonds" });
  }
});

// Check if guest can access a specific companion
router.get('/can-access/:companionId', (req: Request, res: Response) => {
