                  <DropdownMenuSeparator />
                  <DropdownMenuItem onClick={() => setLocation("/dashboard")}>
                    <User className="mr-2 h-4 w-4" />
                    <span>Dashboard</span>
                  </DropdownMenuItem>
                  <DropdownMenuItem onClick={() => setLocation("/admin")}>
                    <svg 
                      xmlns="http://www.w3.org/2000/svg" 
                      width="16" 
                      height="16" 
                      viewBox="0 0 24 24" 
                      fill="none" 
                      stroke="currentColor" 
                      strokeWidth="2" 
                      strokeLinecap="round" 
                      strokeLinejoin="round" 
                      className="mr-2 h-4 w-4"
                    >
                      <path d="M16 21v-2a4 4 0 0 0-4-4H6a4 4 0 0 0-4 4v2" />
                      <circle cx="9" cy="7" r="4" />
                      <path d="M22 21v-2a4 4 0 0 0-3-3.87" />
                      <path d="M16 3.13a4 4 0 0 1 0 7.75" />
                    </svg>
                    <span>Admin Panel</span>
                  </DropdownMenuItem>
                  <DropdownMenuItem onClick={handleLogout}>
                    <LogOut className="mr-2 h-4 w-4" />
                    <span>Log out</span>
                  </DropdownMenuItem>
                </DropdownMenuContent>
              </DropdownMenu>
            )}
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden flex items-center">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="text-white focus:outline-none"
              aria-label="Toggle menu"
            >
              {isOpen ? (
                <X className="h-6 w-6" />
              ) : (
                <Menu className="h-6 w-6" />
              )}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile menu */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: "auto" }}
            exit={{ opacity: 0, height: 0 }}
            transition={{ duration: 0.3 }}
            className="md:hidden bg-[#E91E63] shadow-md absolute w-full overflow-hidden"
          >
            <div className="px-4 pt-3 pb-6 space-y-4">
              <div
                className="block text-white hover:text-white/80 font-medium py-2 text-lg"
                onClick={() => {
                  setIsOpen(false);
                  setLocation("/companions");
                }}
              >
                Companions
              </div>

              {user && (
                <>
                  <div
                    className="block text-white hover:text-white/80 font-medium py-2 text-lg"
                    onClick={() => {
                      setIsOpen(false);
                      setLocation("/dashboard");
                    }}
                  >
                    Dashboard
                  </div>
                  <div
                    className="block text-white hover:text-white/80 font-medium py-2 text-lg"
                    onClick={() => {
                      setIsOpen(false);
                      setLocation("/admin");
                    }}
                  >
                    Admin Panel
                  </div>
                </>
              )}

              <Button
                variant="outline"
                className="w-full bg-white/20 text-white hover:bg-white/30 border-transparent justify-start h-12 font-medium text-lg"
                onClick={() => {
                  setIsOpen(false);
                  setLocation("/membership");
                }}
              >
                Premium
              </Button>

              {!user ? (
                <Button 
                  className="w-full bg-white hover:bg-white/90 text-[#E91E63] font-semibold rounded-lg h-12 text-lg"
                  onClick={() => {
                    setIsOpen(false);
                    setLocation("/auth");
                  }}
                >
                  Login / Sign Up
                </Button>
              ) : (
                <Button 
                  variant="outline"
                  className="w-full bg-white/20 text-white hover:bg-white/30 border-transparent justify-start h-12 font-medium text-lg"
                  onClick={() => {
                    setIsOpen(false);
                    handleLogout();
                  }}
                >
                  <LogOut className="mr-2 h-4 w-4" />
                  Log out
                </Button>
              )}
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </nav>
  );
}

