          {plan.price}
        </span>
        {plan.period && <span className="ml-1 text-muted-foreground">{plan.period}</span>}
      </div>
      <p className="mt-4 text-muted-foreground">
        {plan.description}
      </p>
      <ul className="mt-6 space-y-4">
        {plan.features.map((feature, i) => (
          <li key={i} className={`flex items-start ${feature.included ? "" : "opacity-50"}`}>
            <div className={`flex-shrink-0 w-5 h-5 rounded-full ${
              feature.included ? "bg-primary/10" : "bg-muted"
            } flex items-center justify-center mt-1`}>
              {feature.included ? (
                <Check className="h-3 w-3 text-primary" />
              ) : (
                <X className="h-3 w-3 text-muted-foreground" />
              )}
            </div>
            <p className={`ml-3 ${feature.included ? "text-muted-foreground" : "text-muted-foreground/60"}`}>
              {feature.text}
            </p>
          </li>
        ))}
      </ul>
      <Button
        className={`mt-8 w-full rounded-full ${
          plan.popular
            ? "bg-gradient-to-r from-primary to-secondary text-white"
            : "border-2 border-primary text-primary hover:bg-primary hover:text-white"
        }`}
        variant={plan.popular ? "default" : "outline"}
        onClick={() => setLocation("/signup")}
      >
        {plan.buttonText}
      </Button>
    </motion.div>
  );
}

client/src/components/ProtectedRoute.tsx.content:
import { useAuth } from "@/hooks/use-auth";
import { Loader2 } from "lucide-react";
import { Redirect, Route } from "wouter";

export function ProtectedRoute({
  path,
  component: Component,
}: {
  path: string;
  component: React.ComponentType;
}) {
  const { user, isLoading } = useAuth();

  return (
    <Route path={path}>
      {isLoading ? (
        <div className="flex items-center justify-center min-h-screen">
          <Loader2 className="h-8 w-8 animate-spin text-primary" />
        </div>
      ) : user ? (
        <Component />
      ) : (
        <Redirect to="/auth" />
      )}
    </Route>
  );
}

