                    <Button
                      onClick={handleUploadAlbumImages}
                      disabled={isUpdating}
                      size="sm"
                      className="mt-2 bg-purple-600 hover:bg-purple-700"
                    >
                      {isUpdating ? 'Uploading...' : `Upload ${albumImages.length} Images`}
