import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { z } from "zod";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";

import { Button } from "@/components/ui/button";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Checkbox } from "@/components/ui/checkbox";
import { Input } from "@/components/ui/input";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useAuth } from "@/hooks/use-auth";
import { Loader2 } from "lucide-react";
import { TermsConsentPopup } from "@/components/TermsConsentPopup";

const loginSchema = z.object({
  username: z.string().min(3, "Username must be at least 3 characters"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  rememberMe: z.boolean().default(true),
});

const registerSchema = z.object({
  username: z.string().min(3, "Username must be at least 3 characters"),
  password: z.string().min(6, "Password must be at least 6 characters"),
  confirmPassword: z.string(),
  email: z.string().email("Please enter a valid email"),
  fullName: z.string().optional().or(z.literal("")),
  preferredGender: z.enum(["male", "female", "both"]).default("both"),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords do not match",
  path: ["confirmPassword"],
});

type LoginFormValues = z.infer<typeof loginSchema>;
type RegisterFormValues = z.infer<typeof registerSchema>;

export default function AuthPage() {
  const [, setLocation] = useLocation();
  const { 
    user, 
    loginMutation, 
    registerMutation
  } = useAuth();
  const [activeTab, setActiveTab] = useState<"login" | "register">("login");
  const [savedUsername, setSavedUsername] = useState<string>("");
  const [showTermsConsent, setShowTermsConsent] = useState(false);
  const [pendingRegistrationData, setPendingRegistrationData] = useState<RegisterFormValues | null>(null);

  // Check for saved credentials on component mount
  useEffect(() => {
    const username = localStorage.getItem('savedUsername');
    if (username) {
      setSavedUsername(username);
    }
  }, []);

  // Setup all hooks first, then handle redirects in useEffect
  useEffect(() => {
    // If user is already logged in, redirect to home
    if (user) {
      setLocation("/");
    }
  }, [user, setLocation]);

  const loginForm = useForm<LoginFormValues>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      username: "",
      password: "",
      rememberMe: true,
    },
  });

  // We need to reset the form when savedUsername changes
  useEffect(() => {
    if (savedUsername) {
      loginForm.reset({
        username: savedUsername,
        password: "",
        rememberMe: true
      });
    }
  }, [savedUsername, loginForm]);

  const registerForm = useForm<RegisterFormValues>({
    resolver: zodResolver(registerSchema),
